    private void botonCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonCalcularActionPerformed
        // TODO add your handling code here:
        Float peso= new Float (textoPeso.getText());//con la clase wrapper  envolvemos lo obtenido del text box
        Float altura=new Float(textoAltura.getText());//con esto no asignamos valores el valor se asigna mediante la interfaz
        
        Imc nose=new Imc(peso, altura);
        etiquetaResultado.setText(nose.calcular());
    }//GEN-LAST:event_botonCalcularActionPerformed

